import React, {useReducer, useCallback, useMemo, useRef, useState, useEffect} from "react";
import './App.css';
import DiaryEditor from "./DiaryEditor";
import DiaryList from "./DiaryList";
import OptimizeTest from "./OptimizeTest";

const reducer = (state,action)=>{
    switch(action.type){
         case 'INIT' : {
            return action.data
         }
         case 'REMOVE' : {
            return state.filter((it)=>it.id !== action.targetId);
         }
         case 'EDIT' : {
            return state.map((it)=>it.id===action.targetId?{
                ...it, content: action.newContent
            }:it);
         }
         case 'CREATE' : {
            const created_date = new Date().getTime();
            const newItem = {
                ...action.data,
                created_date
            }
            return [newItem,...state];
         }
         default:
         return state;
    }
}

export const DiaryStateContext = React.createContext();
export const DiaryStateDispatchContext = React.createContext();

function App() {
   const dataId = useRef(0);
   const [data,dispatch] = useReducer(reducer,[]);
   const getData = async()=>{
    const res = await fetch('https://jsonplaceholder.typicode.com/comments').then((res)=>res.json());

    const initData = res.slice(0,20).map((it)=>{
        return{
            author : it.email,
            content : it.body,
            emotion : Math.floor(Math.random()*5)+1,
            created_date : new Date().getTime(),
            id : dataId.current++
        }
    });
    dispatch({type:'INIT', data: initData});
   }

   useEffect(()=>{
    getData();
   },[]);

   const onCreate = useCallback((author, content, emotion) =>{

   dispatch({type:'CREATE',data:{author,content,emotion,id:dataId.current}})
   dataId.current += 1;
   },[]);

   const onRemove = useCallback((targetId)=>{
        dispatch({type:"REMOVE",targetId})
    },[])

   const onEdit = useCallback((targetId, newContent)=>{
        dispatch({type:'EDIT',targetId,newContent});
   },[])

   const getDiaryAnalysis = useMemo(()=>{
     const goodCount = data.filter((it)=>it.emotion>=3).length;
     const badCount = data.length-goodCount;
     const goodRatio = (goodCount / data.length)*100;
     return {goodCount,badCount,goodRatio};
   },[data.length]);

   const {goodCount,badCount,goodRatio} = getDiaryAnalysis;

  return (
        <DiaryStateContext.Provider value={data}>
        <DiaryStateDispatchContext.Provider >
            <div className="App">
                <DiaryEditor onCreate={onCreate}/>
                <div>전체 일기: {data.length}</div>
                <div>기분 좋은 일기 개수 : {goodCount}</div>
                <div>기분 나쁜 일기 개수 : {badCount}</div>
                <div>기분 좋은 일기 비율 : {goodRatio}</div>
                <DiaryList onEdit={onEdit} onRemove={onRemove} diaryList={data}/>
            </div>
        </DiaryStateDispatchContext.Provider>
        </DiaryStateContext.Provider>
  );
}

export default App;
